def menu_screen():
    menu_options = ["Play", "Controls", "Creators", "Exit"]
    option = 0
    menu_text = ps.add_lines(
        ["", "⮞  " + Back.WHITE + Fore.BLACK + "Play" + Style.RESET_ALL + "  ⮜",
         "", "Controls",
         "", "Creators",
         "", "Exit"], title,
        center=True)
    ps.print_screen(menu_text)

    while True:

        # process keyboard input
        key = keyboard.read_event()
        if key.event_type == "up" or key.name not in ("s", "down", "w", "up", "enter", "space"):
            continue
        playsound('sounds/move.mp3', False)
        if key.name in ("s", "down"):
            if option == len(menu_options) - 1:
                option = 0
            else:
                option += 1
        if key.name in ("w", "up"):
            if option == 0:
                option = len(menu_options) - 1
            else:
                option -= 1
        if key.name in ("enter", "space"):
            return menu_options[option]

        # reload menu
        b = title
        for i in range(len(menu_options)):
            b = ps.add_line("", b)
            if i == option:
                b = ps.add_line("⮞  " + Back.WHITE + Fore.BLACK + menu_options[i] + Style.RESET_ALL + "  ⮜", b,
                                center=True)
            else:
                b = ps.add_line(menu_options[i], b, center=True)
        ps.print_screen(b)


def check_sunk(player, enemy):
    """function that checks if a ship has been sunk and returns ship letter and coordinates of each part of the ship

    Args:
        player (Player): the player whose ship_board to check for sunk ships

    Returns:
        tuple: returns a tuple containing letter of ship sunk along with the coordinates of each part of the ship
               e.g. ('p', ((0,1), (0,0)))
    """    

    ships_not_sunk = [i for i in ('c', 'd', 'b', 's', 'p') if i not in player.sunk_ships]

    ship_coords = {'c':[], 'd':[], 'b':[], 's':[], 'p':[]}
    
    for ship in ships_not_sunk:

        count = 0

        for row_idx, row in enumerate(player.ship_board):

            if ship not in row:

                count += 1

                for column_idx, board_value in enumerate(row):

                    if len(board_value) == 2:
                        ship_coords[board_value[0]].append((row_idx, column_idx))

        if count == len(player.ship_board):
            player.sunk_ships.append(ship)
            for y, x in ship_coords[ship]:
                enemy.shot_board[y][x] = '+'
            return (ship, ship_coords[ship])
        

def game():
    # 10 x 10 board grid

    # Ship name     Ship size    Name on board (eg. "c")
    # Carrier	    5            c
    # Battleship    4            b
    # Destroyer	    3            d
    # Submarine	    3            s
    # Patrol Boat   2            p

    # Only strings in the shot boards are "O" for miss, "X" for hit, "+" for sunk, and "-" for empty

    ships = {"c": ["Carrier", 5],
             "b": ["Battleship", 4],
             "d": ["Destroyer", 3],
             "s": ["Submarine", 3],
             "p": ["Patrol board", 2]}

    player = User()
    bot = Bot()

    player.place_ships()
    bot.place_ships()

    is_player_turn = True

    while True:

        if is_player_turn:

            shot = player.take_shot()[::-1]
            logging.debug(shot)
            logging.debug(str(player.ship_board))
            logging.debug(str(player.shot_board))
            logging.debug(str(bot.ship_board))
            logging.debug(str(bot.shot_board))
            result = player.update_boards_on_shot(shot, bot.ship_board)
            logging.debug(result)
            sunk = check_sunk(bot, player)
            top_text = "Player's turn to take a shot"

            if result == "O":
                player.display_shot_board(top_text=top_text, bottom_text="Miss!")
                playsound(r'./sounds/miss.mp3', False)
                sleep(2)

            elif result == "X" and not sunk:
                player.display_shot_board(top_text=top_text, bottom_text="Hit!")
                playsound(r'./sounds/hit.mp3', False)
                sleep(2)

            else:
                player.display_shot_board(top_text=top_text, bottom_text=f"Hit! Sunk {ships[sunk[0]][0]} {ships[sunk[0]][1]}x1")
                playsound(r'./sounds/hit sunk.mp3', False)
                sleep(4)

            is_player_turn = False

        else:

            shot = bot.find_next_shot()
            logging.debug(shot)
            logging.debug(str(player.ship_board))
            logging.debug(str(player.shot_board))
            logging.debug(str(bot.ship_board))
            logging.debug(str(bot.shot_board))
            result = bot.update_boards_on_shot(shot, player.ship_board)
            logging.debug(result)
            sunk = check_sunk(player, bot)
            top_text = "Bot's turn to take a shot"

            if result == "O":
                bot.display_shot_board(top_text=top_text, bottom_text="Miss!")
                playsound(r'./sounds/miss.mp3', False)
                sleep(2)

            elif result == "X" and not sunk:
                bot.display_shot_board(top_text=top_text, bottom_text="Hit!")
                playsound(r'./sounds/hit.mp3', False)
                sleep(2)

            else:
                bot.display_shot_board(top_text=top_text, bottom_text=f"Hit! Sunk {ships[sunk[0]][0]} {ships[sunk[0]][1]}x1")
                playsound(r'./sounds/hit sunk.mp3', False)
                sleep(4)

            is_player_turn = True

        if set(player.sunk_ships) == {"c", "b", "d", "s", "p"}:
            ps.print_screen(bot_wins)
            keyboard.wait('enter')
            break

        elif set(bot.sunk_ships) == {"c", "b", "d", "s", "p"}:
            ps.print_screen(player_wins)
            keyboard.wait('enter')
            break


if __name__ == "__main__":
    while True:
        choice = menu_screen()
        if choice == "Play":
            game()
        if choice == "Controls":
            ps.print_screen(controls)
            keyboard.wait('enter')
        if choice == "Creators":
            ps.print_screen(creators)
            keyboard.wait('enter')
        if choice == "Exit":
            break
